import './App.css';
import { useState } from 'react';

function App() {
	const [hoveredPage, setHoveredPage] = useState(null);

	const pages = [
		{ id: 'home', title: '홈', content: '홈 페이지 내용입니다.' },
		{ id: 'about', title: '소개', content: '소개 페이지 내용입니다.' },
		{ id: 'services', title: '서비스', content: '서비스 페이지 내용입니다.' },
		{ id: 'contact', title: '연락처', content: '연락처 페이지 내용입니다.' }
	];

	return (
		<div className="App">
			<nav className="navigation">
				<ul>
					{pages.map(page => (
						<li 
							key={page.id}
							onMouseEnter={() => setHoveredPage(page)}
							onMouseLeave={() => setHoveredPage(null)}
						>
							{page.title}
						</li>
					))}
				</ul>
			</nav>

			<div className="content-area">
				{hoveredPage && (
					<div className="hover-page">
						<h2>{hoveredPage.title}</h2>
						<p>{hoveredPage.content}</p>
					</div>
				)}
			</div>
		</div>
	);
}

export default App;
