import './App.css';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { useState } from 'react';

// 페이지 컴포넌트들 import
import Jmcoffee from './pages/Jmcoffee';
import Highziumstudio from './pages/Highziumstudio';
import Purito from './pages/Purito';
import Sum37 from './pages/Sum37';
import Mennoblesse from './pages/Mennoblesse';
import Klaytn from './pages/Klaytn';
import Kitchen from './pages/Kitchen';

function Navigation() {
	const [hoveredPage, setHoveredPage] = useState(null);
	const location = useLocation();

	const pages = [
		{ id: 'jmcoffee', title: 'jmcoffee', path: '/jmcoffee', component: Jmcoffee },
		{ id: 'highziumstudio', title: 'highziumstudio', path: '/highziumstudio', component: Highziumstudio },
		{ id: 'purito', title: 'purito', path: '/purito', component: Purito },
		{ id: 'sum37', title: 'sum37', path: '/sum37', component: Sum37 },
		{ id: 'mennoblesse', title: 'mennoblesse', path: '/mennoblesse', component: Mennoblesse },
		{ id: 'klaytn', title: 'klaytn', path: '/klaytn', component: Klaytn },
		{ id: 'kitchen', title: 'kitchen', path: '/kitchen', component: Kitchen }
	];

	return (
		<div className="App">
			<nav className="navigation">
				<ul>
					{pages.map(page => (
						<li 
							key={page.id}
							className={location.pathname === page.path ? 'active' : ''}
							onMouseEnter={() => setHoveredPage(page)}
							onMouseLeave={() => setHoveredPage(null)}
						>
							<Link to={page.path}>{page.title}</Link>
						</li>
					))}
				</ul>
			</nav>

			<div className="content-area">
				{hoveredPage && (
					<div className="hover-page">
						<h2>{hoveredPage.title}</h2>
						<p>{hoveredPage.title} 프로젝트 내용입니다.</p>
					</div>
				)}
			</div>
		</div>
	);
}

function App() {
	return (
		<Router>
			<Routes>
				<Route path="/" element={<Navigation />} />
				<Route path="/jmcoffee" element={<Jmcoffee />} />
				<Route path="/highziumstudio" element={<Highziumstudio />} />
				<Route path="/purito" element={<Purito />} />
				<Route path="/sum37" element={<Sum37 />} />
				<Route path="/mennoblesse" element={<Mennoblesse />} />
				<Route path="/klaytn" element={<Klaytn />} />
				<Route path="/kitchen" element={<Kitchen />} />
			</Routes>
		</Router>
	);
}

export default App;
