import './App.css';
import { useState } from 'react';

function App() {
	const [hoveredPage, setHoveredPage] = useState(null);

	const pages = [
		{ id: 'jmcoffee', title: 'jmcoffee', content: 'jmcoffee 프로젝트 내용입니다.' },
		{ id: 'highziumstudio', title: 'highziumstudio', content: 'highziumstudio 프로젝트 내용입니다.' },
		{ id: 'purito', title: 'purito', content: 'purito 프로젝트 내용입니다.' },
		{ id: 'sum37', title: 'sum37', content: 'sum37 프로젝트 내용입니다.' },
		{ id: 'mennoblesse', title: 'mennoblesse', content: 'mennoblesse 프로젝트 내용입니다.' },
		{ id: 'klaytn', title: 'klaytn', content: 'klaytn 프로젝트 내용입니다.' },
		{ id: 'kitchen', title: 'kitchen', content: 'kitchen 프로젝트 내용입니다.' }
	];

	return (
		<div className="App">
			<nav className="navigation">
				<ul>
					{pages.map(page => (
						<li 
							key={page.id}
							onMouseEnter={() => setHoveredPage(page)}
							onMouseLeave={() => setHoveredPage(null)}
						>
							{page.title}
						</li>
					))}
				</ul>
			</nav>

			<div className="content-area">
				{hoveredPage && (
					<div className="hover-page">
						<h2>{hoveredPage.title}</h2>
						<p>{hoveredPage.content}</p>
					</div>
				)}
			</div>
		</div>
	);
}

export default App;
